package testdata;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//to print 1-10
    for (int a=1; a<=10; ++a) {
    	System.out.println(a);
    }
		
		//to print 3 table
  
    
    for(int a=1; a<=10;  a++) {
    	System.out.println(a*3);
    }
    //to print alternate numbers  
    
		for(int a=2; a<=10; a+=2) {
			System.out.println(a);
			
		}
		//to print factorial
		int num=5;
		long fact=1;
		for(int i=1;i<=num;i++) {
			fact=fact*i;
		}
		System.out.println(fact);	
	for( int i=10; i>0; i--) {
		System.out.println(i);}
	//int num1=1;
	int f= 9;
	for(int i=1; i<=9;i++) {
		f=f*i;}System.out.println(f);
	}
	}

			
		

	
		
