package testdata;

public class Switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
              int option =1;
              int a=10;
              int b=20;
              switch(option){ 
              case 1:
            	  System.out.println("Add" +a+b);
            	  break;
              case 2:
            	  System.out.println("sub" +(a-b));
            	  break;
              case 3:
            	  System.out.println("multi" +(a*b));
            	  break;
              case 4:
            	  System.out.println("div" +(a/b));
            	  break;
            	  
              }
            	  
            	  

}}
