package testdata;

public class Switchcaseextra {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int option =1;
		switch (option) {
		case 1:
			System.out.println("HI");
		case 2:
			System.out.println("welcome to SQL");
		case 3:
			System.out.println("TODAY");
		case 4:
			System.out.println("WE WILL BE");
		case 5:
			System.out.println("WORKING HARD");
		case 6: System.out.println("GOOD LUCK");
		case 7: System.out.println ("...BYE");
	}
		int a= 20;
		int b= 30;
		int opt =1;
		switch (opt) {
		case 1:
			System.out.println("add " +(a+b));break;
		case 2:
			System.out.println("sub "+( a-b) );break;
		}
		
	}
}